func check(string: [Character], lineLength: Int) -> Int{
    var s = 0
    var i = 0
    while i < (string.count - 2*(lineLength) - 1) {
        if String(string[i]) == "M" && String(string[i+2]) == "M" && String(string[i + (2 * lineLength)]) == "S" && String(string[i + (2 * lineLength) + 2]) == "S" && String(string[i + lineLength + 1]) == "A" {
            print(i, "one")
            s += 1
        } else if String(string[i]) == "S" && String(string[i+2]) == "S" && String(string[i + (2 * lineLength)]) == "M" && String(string[i + (2 * lineLength) + 2]) == "M" && String(string[i + lineLength + 1]) == "A" {
            print(i, "two")
            s += 1
        } else if String(string[i]) == "M" && String(string[i+2]) == "S" && String(string[i + (2 * lineLength)]) == "M" && String(string[i + (2 * lineLength) + 2]) == "S" && String(string[i + lineLength + 1]) == "A" {
            print(i, "three")
            s += 1
        } else if String(string[i]) == "S" && String(string[i+2]) == "M" && String(string[i + (2 * lineLength)]) == "S" && String(string[i + (2 * lineLength) + 2]) == "M" && String(string[i + lineLength + 1]) == "A" {
            print(i, "four")
            s += 1
        }
        i += 1
    }
    return s
}

print(check(string: inputs, lineLength: 141))